module.exports = function (fileName, checksum, callback) {
    var crypto = require('crypto');
    var fs = require('fs');

    var algo = 'md5';
    var shasum = crypto.createHash(algo);

    var s = fs.ReadStream(fileName);
    s.on('data', function (d) {
        shasum.update(d);
    });
    s.on('end', function () {
        var d = shasum.digest('hex');
        console.log(d == checksum);
        console.log(d, checksum);
        if (typeof callback == "function") callback(d == checksum);
    });

}